import './Hero.scss';
import { useEffect } from 'react';
import SplitType from 'split-type';
import { gsap } from 'gsap';
import sparkle from '../assets/sparkle.svg';

export default function Hero({ className }) {

    useEffect(() => {
        const myText = new SplitType('.intro-name');

        gsap.to('.char', {
            y: 0,
            stagger: 0.1,
            delay: 1.5,
            duration: .1
        });

        gsap.fromTo('.sparkle', { width: 'initial size' }, {
            width: '5em',
            height: 'auto',
            delay: 1,
            duration: 2,
            ease: 'power3.out' // Use a smoother easing function
        });
    });

    return (
        <div className={className} id='home-intro' >
            <div id='intro-words'>
                <div className='clipy intro-name'>Aashna</div>
                <div className='clipy intro-name'>VERMA</div>
            </div>
            <div className='intro-sparkle'>
                <img className='sparkle' src={sparkle} />
            </div>
        </div>
    );
}
