import './Home.scss';
import Project from '../components/Project';
import Hero from '../components/Hero';
import { gsap } from 'gsap';
import { useEffect } from 'react';
import SplitType from 'split-type';
import img from '../assets/projects/fireRacoon.png';
import { Tooltip } from 'react-tooltip';


function Home() {

	useEffect(() => {
		console.log('mounted');
		gsap.to('.block', {
			opacity: 0,
			duration: 1.5,
			delay: 1,
			zIndex: -1
		});

		setTimeout(() => {
			const myText1 = new SplitType('.line-popup', { types: 'lines', lineClass: 'clipy-line-popup clipy' });
			const myText2 = new SplitType('.clipy-line-popup ', { types: 'lines', lineClass: 'upsie' });

			var upsies = document.querySelectorAll('.upsie');
			var popup = document.querySelectorAll('.project-popup');

			upsies.forEach(upsie => {
				gsap.timeline({
					scrollTrigger: {
						trigger: upsie,
						start: 'top bottom',
						end: '+=100 bottom',
						scrub: true,
						markers: false
					}
				}).to(upsie, {
					y: 0
				});
			});

			popup.forEach(upsie => {
				gsap.timeline({
					scrollTrigger: {
						trigger: upsie,
						start: '-100% 97%',
						end: '-80% 97%',
						scrub: true,
						markers: false
					}
				}).to(upsie, {
					y: 0
				});
			});
		}, 50);

		gsap.fromTo('#gradient', {
			scrollTrigger:
			{
				trigger: '#home-about',
				start: '30% 30%',
				scrub: true,
			},
			left: '0',
			width: '85vh',
			height: '85vh',
			borderRadius: '50%',
		},
			{
				scrollTrigger:
				{
					trigger: '#home-about',
					start: '30% 30%',
					end: '80% 30%%',
					markers: false,
					scrub: true,
				},
				left: 'calc(-30% + 10px)',
				width: '40%',
				height: '100%',
				borderRadius: '0',
			});

		gsap.fromTo('#gradient', {
			scrollTrigger: {
				trigger: '#home-intro',
				start: 'top top',
				scrub: true,
			},
			width: '100%',
			height: '100%',
			borderRadius: '0',
		},
			{
				scrollTrigger: {
					trigger: '#home-intro',
					start: '30% 30%',
					end: '80% 30%%',
					markers: false,
					scrub: true,
				},
				width: '85vh',
				height: '85vh',
				borderRadius: '50%',
			});

	});

	const _technologies = [
		{ tooltip: 'NodeJS', icon: 'devicon-nodejs-plain' },
		{ tooltip: 'React', icon: 'devicon-react-original' },
		{ tooltip: 'Blazor', icon: 'devicon-blazor-original' },
		{ tooltip: 'Git', icon: 'devicon-git-plain' }
	];

	const _languages = [
		{ tooltip: 'Java', icon: 'devicon-java-plain' },
		{ tooltip: 'C#', icon: 'devicon-csharp-plain' },
		{ tooltip: 'JavaScript', icon: 'devicon-javascript-plain' },
		{ tooltip: 'Python', icon: 'devicon-python-plain' },
		{ tooltip: 'C++', icon: 'devicon-cplusplus-plain' },
		{ tooltip: 'Sass', icon: 'devicon-sass-original' }
	];

	const _funsies = [
		{ tooltip: 'Notion', icon: 'devicon-notion-plain' },
		{ tooltip: 'Figma', icon: 'devicon-ashicon figma-outline' },
		{ tooltip: 'Illustrator', icon: 'devicon-illustrator-plain' },
		{ tooltip: 'Procreate', icon: 'devicon-ashicon procreate-filled' }
	];

	//select and put an onhover event listener on html i
	//when hovered over, show tooltip
	document.querySelectorAll('a').forEach(item => {
		console.log("here");
		item.addEventListener('mouseover', () => {
			let element = document.getElementById('ash-tooltip');
			let currentTop = parseInt(element.style.top, 10);
			console.log(1000);
			element.style.top = (currentTop - 10) + 'px';
		});
	});


	// headers for each section
	const Header = (a, b, c) => {
		return (
			<div className='clipy'>
				<div className='upsie header-paragraph'>
					<div className='home-header'>
						<h2 className='header-number'>{a}</h2>
						<h2 className='header-title'>{b}</h2>
					</div>
					<p className='header-p'>{c}</p>
				</div>
			</div>
		);
	};

	const Skills = (title, icons) => {
		return (
			<div className='line-popup skill'>
				<h3 className='skill-title'>{title}</h3>
				<div className='skill-icons'>
					{icons.map(item => {
						return (
							<a
								data-tooltip-id="ash-tooltip"
								data-tooltip-content={item.tooltip}
								data-tooltip-place="bottom"
							>
								<i className={item.icon} />
							</a>

						);
					})}
				</div>
			</div>
		);
	};

	return (
		<div className='Home'>

			<Hero />

			<div className='home-section' id='home-about'>
				{Header('./01', 'About', 'I\'m a Software Engineering student @ CARLETON UNIVERSITY. Merging creativity with technology to create functional and aesthetic systems.')}
				<div className='skills'>
					{Skills('Technologies', _technologies)}
					{Skills('Languages', _languages)}
					{Skills('Funsies', _funsies)}
				</div>
			</div>

			<div className='home-section' id='home-projects'>
				{Header('./02', 'Recent projects')}
				<Project img={img} title='ColorMe' tags={['FrontEnd', 'BackEnd', 'turkey', 'chciken', 'sdjfhskjdhfskd']} links={[['Github', 'https://github.com/Isabella-Nguyen/ColourMe']]} />
				<Project img={img} title='cuHacking' tags={['FrontEnd', 'Back']} />
				<Project img={img} title='ColorMe' tags={['FrontEnd', 'Back']} links={[['Github', 'https://github.com/Isabella-Nguyen/ColourMe']]} />
			</div>

			<div id='home-projects'>
				{Header('./03', 'contact me')}
			</div>

			<div className='space'></div>
			<Tooltip id="ash-tooltip" />
		</div>
	);
}

export default Home;;
