import './Project.scss';
import { useEffect } from 'react';
import { gsap } from 'gsap';
import arrow from '../../assets/arrow.svg';


function Project(img, title, links, tags) {

    useEffect(() => {
        const buttons = document.querySelectorAll('.arrow');
        // buttons.forEach(button => {
        //     button.addEventListener('mouseenter', () => {
        //         gsap.to(button.querySelector('img'), {
        //             opacity: 1,
        //             width: '2em',
        //             height: '0.9em',
        //             paddingLeft: '0.5em',
        //             duration: 0.5,
        //         });
        //     });
        //     button.addEventListener('mouseleave', () => {
        //         gsap.to(button.querySelector('img'), {
        //             opacity: 0,
        //             paddingLeft: '0em',
        //             width: '0em',
        //             height: '0em',
        //             duration: 0.7
        //         });
        //     });
        // });
    });

    return (
        <div className='clipy'>
            <div className='project upsie project-popup'>
                <img className='project-image' src={img} alt="project" />
                <div className='project-info'>
                    <div className='project-title'>{title}</div>

                    {links ? links.map(([tag, link], index) => (
                        <button key={`${tag}-${index}`} className='project-tag arrow' onClick={() => window.open(link, '_blank')}>
                            <span>{tag}</span>
                            <img src={arrow} alt="Arrow" aria-label="Arrow" />
                        </button>
                    )) : null}
                    {tags ? tags.map((tag, index) => (
                        <div key={`${tag}-${index}`} className='project-tag roundHex'>
                            <span>{tag}</span>
                        </div>
                    )) : null}
                    
                </div>
            </div>
        </div>
    );
}

export default Project;
