import './Project.css';
import { useEffect } from 'react';
import { gsap } from 'gsap';


function Project({img, title}) {

    useEffect(() => {
        var popup = document.querySelectorAll('.project-popup');

		popup.forEach(upsie => {
			var tl = gsap.timeline({
				scrollTrigger: {
					trigger: upsie,
					start: 'top 97%',
					end: 'top 97%',
					scrub: true,
					markers: true
				}
			}).to(upsie, {
				y: 0,
				stagger: 0.05,
				delay: 1,
				duration: .1
			});
		});
     });

    return (
        <div className='clipy'>
            <div className='project upsie project-popup'>
                <img className='project-image' src={img} />
                <div className='project-title'>{title}</div>
            </div>
        </div>
    );
}

export default Project;
