import './Project.scss';
import { useEffect } from 'react';
import { gsap, animation} from 'gsap';
import arrow from '../assets/arrow.svg';


function Project({ img, title, tags, links }) {

    useEffect(() => {
        //when hover on button, make the arrow apear using gsap
        //make the arrow only appear on hover, then return to its original state using gsap





        const buttons = document.querySelectorAll('.arrow');
        buttons.forEach(button => {
            button.addEventListener('mouseenter', () => {
                gsap.to(button.querySelector('img'), {
                    width: '2em',
                    height: '2em',
                    backgroundColor: 'white',
                    duration: .2
                });
            });
            animation.revert(button);
        });
    });

    return (
        <div className='clipy'>
            <div className='project upsie project-popup'>
                <img className='project-image' src={img} />
                <div className='project-info'>
                    <div className='project-title'>{title}</div>

                    {links ? links.map(([tag, link]) => <button className='project-tag arrow' onClick={() => window.open(link, '_blank')} >{tag}<div className='icon'><img src={arrow}/></div></button>) : null}
                    {tags ? tags.map(tag => <span className='project-tag roundHex'>{tag}</span>) : null}
                    
                </div>
            </div>
        </div>
    );
}

export default Project;
