import './Background.scss';
import gradient from '../assets/gradient.png';

export default function Background() {

    useEffect(() => {
        // Ensure grained.js is loaded
        if (window.grained) {
            const options = {
                animate: true,
                patternWidth: 100,
                patternHeight: 100,
                grainOpacity: 0.05,
                grainDensity: 1,
                grainWidth: 1,
                grainHeight: 1,
            };
            window.grained('#background', options);
        }
    }, []);

    return (
        <div id='background'>
            <script src="grained.js"></script>
            <div className='grained'></div>
            <img id='gradient' src={gradient} />
        </div>
    );
}
