import './Home.css';
import Project from '../components/Project';
import { gsap } from 'gsap';
import { ScrollTrigger } from "gsap/ScrollTrigger";
import { useEffect } from 'react';
import SplitType from 'split-type';
import img from '../assets/projects/fireRacoon.png';

function Home() {

	useEffect(() => {
		const myText = new SplitType('.intro-name');
		const myText2 = new SplitType('#intro-role', { types: 'words' });

		gsap.to('.char', {
			y: 0,
			stagger: 0.05,
			delay: 0.2,
			duration: .1
		});

		gsap.to(myText2.words, {
			y: 0,
			stagger: 0.05,
			delay: 1,
			duration: .1
		});

		// gsap.to('.home-header', {
		// 	y: 0,
		// 	stagger: 0.05,
		// 	delay: 1,
		// 	duration: .1
		// });




		

		gsap.registerPlugin(ScrollTrigger) 

		var tl = gsap.timeline({
			// defaults: { duration: 1, ease: 'none' },
			// scrollTrigger: {
			// 	trigger: 'svg',
			// 	pin: true,
			// 	start: 'top top',
			// 	end: '+=2000', // total time of scrub animation in px
			// 	scrub: 0.3,
			// 	onUpdate(self) {
			// 		if (self.direction === -1 && self.progress > 0.85) {
			// 			self.scroll(self.start);
			// 		}
			// 		console.log(self.progress);
			// 	}

			// }
		}).to('.home-header', {
				y: 0,
				stagger: 0.05,
				delay: 1,
				duration: .1
			});





	});

	const header = (a, b) => {
		return (
			<div className='home-header'>
				<h2 className='header-number'>{a}</h2>
				<h2 className='header-title'>{b}</h2>
			</div>);
	};

	return (
		<div className='Home'>
			<div id='intro'>
				<div>
					<div id='intro-words'>
						<div className='intro-name'>AASHNA</div>
						<div className='intro-name'>VERMA</div>
						<div className='upsie' id='intro-role'>- Software engineering student -</div>
					</div>
					<div className='circle '></div>
				</div>
			</div>
			<div id='home-about'>
				{header('./1', 'about me')}
				<p>A dedicated Software Engineering student @ CARLETON UNIVERSITY. constantly creating and learning. Merging creativity with technology to
					create functional and aesthetic systems.\n\nOutside of coding, you will find me drawing or crocheting something elaborate.</p>
			</div>
			<div id='home-projects'>
				{header('./2', 'recent projects')}
				<Project img={img} title='ColorMe' />
				<Project img={img} title='cuHacking' />
				<Project img={img} title='ColorMe' />
			</div>
			<div id='home-projects'>
				{header('./3', 'contact me')}
			</div>
		</div>
	);
}

export default Home;
